// dependencies
var fs = require("fs")
var postcss = require("postcss")
var atImport = require("postcss-import")

// css to be processed
var css = fs.readFileSync("test/fixtures/testing", "utf8")
console.log('CWD:' + process.cwd());
// process css
var output = postcss()
  .use(atImport({
    path: ['test/fixtures']
  }))
  .process(css)
  .css
  
console.log(output);